        rnavTypeFlags !== undefined && this.approachDetails.set('rnavTypeFlags', rnavTypeFlags);
        isCircling !== undefined && this.approachDetails.set('isCircling', isCircling);
        isVtf !== undefined && this.approachDetails.set('isVtf', isVtf);
